let ERC_contract = web3.eth.contract(erc20_abi);
const user_addr = web3.eth.accounts[0];
ERC_contract.eth.defaultAccount = web3.eth.accounts[0];


const eth_token_addr = '0x0000000000000000000000000000000000000000';
const amount_approved =  '1000000000000000000';

var ERC = ERC_contract.at(eth_token_addr);


// console.log(ED.balanceOf(eth_token_addr, web3.eth.accounts[0])['c'].toString());
ERC.approve(user_addr, amount_approved);
ED.deposit({value: amount_approved});

console.log(ED.balanceOf(eth_token_addr, user_addr).toString());